from sklearn.metrics import confusion_matrix

def plot_confusion_matrix_heatmap(y_test, y_pred):
    # Generate the confusion matrix
    cm = confusion_matrix(y_test, y_pred)

    # Create a heatmap using Seaborn
    plt.figure(figsize=(6, 5))
    sns.heatmap(cm, annot=True, fmt='g', cmap='Blues', xticklabels=['Ham', 'Spam'], yticklabels=['Ham', 'Spam'])

    # Set labels and title
    plt.xlabel('Predicted')
    plt.ylabel('True')
    plt.title('Confusion Matrix Heatmap')

    # Show the plot
    plt.show()

plot_confusion_matrix_heatmap(y_test, y_pred_linear)

"""#Confusion-Matrix -- RBF Kernel

displaying a confusion matrix for the RBF kernel, it works similarly to the linear kernel but requires a classification model (SVC, not SVR).
"""

plot_confusion_matrix_heatmap(y_test, y_pred_rbf)

"""#Confusion Matrix - Polynomial Kernel"""

plot_confusion_matrix_heatmap(y_test, y_pred_poly)

